import {
  AUTH_ERROR,
  LOGIN_SUCCESS,
  LOGIN_FAIL,
} from '../actions/types';

const initialState = {
  token: localStorage.getItem('token'),
  isAuthenticated: null,
  loading: true,
  user: null
};

export default function(state = initialState, action) {
  const { type } = action;

  switch (type) {

    case LOGIN_SUCCESS:
      // localStorage.setItem('token', payload.token);
      return {
        ...state,

        isAuthenticated: true,
      };

    case AUTH_ERROR:
    case LOGIN_FAIL:
    default:
      return state;
  }
}
